import React, { useState } from "react";
import Navbar from "../components/navbar";
import Footer from "../components/footer";
import "../css/shared_css/inputform.css";
import "../css/shared_css/table.css";
import "../css/shared_css/herobutton.css";
import "../css/live.css";

/*eslint-disable jsx-a11y/anchor-is-valid*/

function Live() {  

    const [tableRows, setTableRows] = useState(sessionStorage.getItem("liveTableRows") || "");
    const [total, setTotal] = useState(parseInt(sessionStorage.getItem("liveTotal") || 0));
  
