import React, { useState, useEffect, useRef } from "react";
import { Chart as ChartJS } from "chart.js";
import { NavLink } from "react-router-dom";
import "../css/shared_css/navbar.css";
import "../css/shared_css/footer.css";
import "../css/shared_css/progress_bar.css";
import "../css/shared_css/table.css";
import "../css/summary.css";

/*eslint-disable jsx-a11y/anchor-is-valid*/
function Summary() {
        const navLinks = document.getElementById("navLinks");

            function showMenu() {
                navLinks.style.right = "0";
            }

            function hideMenu() {
                navLinks.style.right = "-200px";
            }

            
            const button1 = useRef(document.getElementsByClassName("button1"));
            const button2 = useRef(document.getElementsByClassName("button2"));

            useEffect(() => {
                highlightButton(button1.current);
                highlightButton(button2.current);
            }, []);

            function highlightButton(button) {
                for (let i = 0; i < button.length; i++) {
                    button[0].style.background = 'rgba(4, 59, 92, 0.7)';
                    button[0].style.color = '#fff';

                    button[i].addEventListener("click", function (){
                        if (button[i].style.background === 'rgba(4, 59, 92, 0.7)') {
                            button[i].style.background = 'transparent';
                            button[i].style.color = 'black';
                        } else {
                            button[i].style.background = 'rgba(4, 59, 92, 0.7)';
                            button[i].style.color = '#fff';
                        }
                    });
                }
            }

            const live_bar = useRef(document.getElementById("live_bar"));
            const give_bar = useRef(document.getElementById("give_bar"));
            const grow_bar = useRef(document.getElementById("grow_bar"));
            const owe_bar = useRef(document.getElementById("owe_bar"));

            const live_sum = useRef(document.getElementById('live_bar').getElementsByTagName('h2'));
            const give_sum = useRef(document.getElementById("give_bar").getElementsByTagName('h2'));
            const grow_sum = useRef(document.getElementById("grow_bar").getElementsByTagName('h2'));
            const owe_sum = useRef(document.getElementById("owe_bar").getElementsByTagName('h2'));



            /** Links to other pages ***/

            live_bar.onClick = () => { window.location.href = "/live.js#form-header"; }
            give_bar.onClick = () => { window.location.href = "/give.js#form-header"; }
            grow_bar.onClick = () => { window.location.href = "/grow.js#form-header"; }
            owe_bar.onClick = () => { window.location.href = "/owe.js#form-header"; }


            let circularProgress = document.querySelector(".circular-progress"),
                progressValue = document.querySelector(".progress-value");

            let income_input = document.getElementById("income_input");

            let taxValue = 0;
            let after_tax = document.getElementById("after_tax");
            let after_tax_num = document.getElementById("after_tax_num");

            let i_sum = 0;
            let l_sum = 0;
            let g_sum = 0;
            let gr_sum = 0;
            let o_sum = 0;
            let total_expenses_num = document.getElementById("total_expenses_num");

            //add $ sign to beginning of input
            income_input.addEventListener("focus", function()
            {
                if(income_input.value.charAt(0) != '$'){
                    income_input.value = '$';
                }   
                
            });






            // the is the functionalites for the social media icons
            function showFBpage() {
                window.location.href = "https://www.facebook.com/groups/LibertyCFL/?_ga=2.101789572.488967955.1669654119-816072560.1565226602";
            }

            function showTWpage() {
                window.location.href = "https://twitter.com/LibertyU_Busi?_ga=2.101985156.488967955.1669654119-816072560.1565226602";
            }

            function showIGpage() {
